Page._content:
#waifu-toggle {
	background-color: #fa0;
	border-radius: 5px;
	bottom: 66px;
	cursor: pointer;
	display: flex;
	justify-content: flex-end;
	left: 0;
	margin-left: -100px;
	padding: 5px;
	position: fixed;
	transition: margin-left 1s;
	width: 50px;
}

#waifu-toggle.waifu-toggle-active {
	margin-left: -50px;
}

#waifu-toggle.waifu-toggle-active:hover {
	margin-left: -30px;
}

#waifu-toggle svg {
	//无效？
	fill: #ffffff;
	height: 25px;
}

#waifu {
	bottom: 500px;
	left: -10px;
	position: fixed;
	transform: translateY(25px);
	transition: transform .3s ease-in-out, bottom 5s ease-in-out;
	z-index: 1;
}

#waifu.waifu-active {
	bottom: 20px;
}

#waifu.waifu-hidden {
	display: none;
}

#waifu:hover {
	transform: translateY(20px);
}

#waifu-tips {
	animation: waifu-shake 20s ease-in-out 5s infinite;
	background-color: rgba(236, 217, 188, .5);
	border: 1px solid rgba(224, 186, 140, .62);
	border-radius: 12px;
	box-shadow: 0 3px 15px 2px rgba(191, 158, 118, .2);
	font-size: 14px;
	line-height: 24px;
	margin: -60px 20px;
	min-height: 80px;
	opacity: 0;
	overflow: hidden;
	padding: 5px 10px;
	position: absolute;
	text-overflow: ellipsis;
	transition: opacity 1s;
	width: 160px;
	word-break: break-all;
}

#waifu-tips.waifu-tips-active {
	opacity: 1;
	transition: opacity .2s;
}

#waifu-tips span {
	color: #0099cc;
}

#live2d {
	cursor: grab;
	height: 200px;
	position: relative;
	width: 200px;
}

#live2d:active {
	cursor: grabbing;
}

#waifu-tool {
	align-items: center;
	display: flex;
	flex-direction: column;
	gap: 5px;
	opacity: 0;
	position: absolute;
	right: 0px;
	bottom: 10px;
	transition: opacity 1s;
}

#waifu:hover #waifu-tool {
	opacity: 1;
}

#waifu-tool svg {
	cursor: pointer;
	display: block;
	fill: #303030;
	height: 20px;
	transition: fill .3s;
}

#waifu-tool svg:hover {
	fill: #0684bd; /* #34495e */
}

@keyframes waifu-shake {
	2% {
		transform: translate(.5px, -1.5px) rotate(-.5deg);
	}
	4% {
		transform: translate(.5px, 1.5px) rotate(1.5deg);
	}
	6% {
		transform: translate(1.5px, 1.5px) rotate(1.5deg);
	}
