            this.preloadMotionGroup(LAppDefine.MOTION_GROUP_IDLE);
            this.mainMotionManager.stopAllMotions();

            this.setUpdating(false);
            this.setInitialized(true);

            if (typeof callback == 'function') callback();
          }
        });
      }
    });
  }

